import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import App from "./App";
import reportWebVitals from "./reportWebVitals";
import { createStore } from "redux";

// ACTIONS
const incrementar = () => {
  return {
    type: "INCREMENTAR",
  };
};

const disminuir = () => {
  return {
    type: "DISMINUIR",
  };
};

// REDUCERS
const counter = (state = 0, action) => {
  switch (action.type) {
    case "INCREMENTAR":
      return state + 1;
    case "DISMINUIR":
      return state - 1;
    default:
      return state;
  }
};

let store = createStore(counter);

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);

reportWebVitals();
